mapper.select:
select if(end_at <= now(), true, false)
from contest
where contest_id = #{contestId};

mapper.update:
update contest
set contest_name = #{contestName},
    start_at     = #{startAt},
    end_at       = #{endAt},
    languages    = #{languages}
where contest_id = #{contestId}

mapper.delete:
delete
from `contest-problem`
where contest_id = #{contestId}
  and problem_id = #{problemId}
