mapper.select:
select max(score) as score
from solution
where user_id = #{userId}
and problem_id = #{problemId}

    and contest_id = #{contestId}
    and contest_id is null

mapper.update:
update solution
set state     = #{state},
    result    = #{result},
    pass_rate = #{passRate},
    score     = #{score}
where solution_id = #{solutionId}
